mapper.insert:
INSERT
  INTO WC_MEMORY
(
       MEMORY_ID
     , CONTENT
     , TITLE
     , CATEGORY
     , TODO_YN
     , MEMBER_ID
)
VALUES (
       SEQ_MEMORY_ID.NEXTVAL
     , #{content}
     , #{title} 
     , #{category} 
     , #{todoYn}
     , 'woochan'
)

INSERT
  INTO WC_TODO
     (
       MEMORY_ID
     , STR_DATE 
     , END_DATE 
     , SUCC_DATE
     )
VALUES
     (
       #{memoryId}
     , #{strDate} 
     , #{endDate} 
     , #{succDate}
     )

mapper.update:
UPDATE WC_TODO
   SET SUCC_DATE = #{succDate}
 WHERE MEMORY_ID = #{memoryId}

UPDATE WC_MEMORY
   SET SHOW_DIV  = 'N'
 WHERE MEMORY_ID = #{memoryId}

UPDATE WC_MEMORY
   SET CONTENT     = #{content}
     , TITLE       = #{title}    
     , CATEGORY    = #{category} 
     , TODO_YN     = #{todoYn}   
     , MEMBER_ID   = 'woochan'   
     , MODIFY_DATE = SYSDATE  
 WHERE MEMORY_ID = #{memoryId}
